defmodule NotQuiteLispTest do
  use ExUnit.Case
  doctest NotQuiteLisp

  setup do
    pid = NotQuiteLisp.start_link
    {:ok, %{pid: pid}}
  end

  test "it can get teh current floor", %{pid: pid} do
    assert NotQuiteLisp.current_floor(pid) == 0
  end

  test "it can get the current iteration", %{pid: pid} do
    NotQuiteLisp.process_directions(pid, "((((((")
    assert NotQuiteLisp.current_iteration(pid) == 6
  end

  test "it can process a single direction to go up", %{pid: pid} do
    NotQuiteLisp.process_direction(pid, "(")
    assert NotQuiteLisp.current_floor(pid) == 1
  end

  test "it can process a single direction to go down", %{pid: pid} do
    NotQuiteLisp.process_direction(pid, ")")
    assert NotQuiteLisp.current_floor(pid) == -1
  end

  test "it can process several directions at once", %{pid: pid} do
    NotQuiteLisp.process_directions(pid, "((")
    assert NotQuiteLisp.current_floor(pid) == 2
  end

